
$ErrorActionPreference = "Stop"

function Clear-Signal {
    [CmdletBinding()]
    param(
        [string]
        $Signal,

        [int[]]
        $Pattern = @(0, 1, 0, -1)
    )

    $outputSignal = ""
    for ($outIndex = 0; $outIndex -lt $Signal.Length; $outIndex++) {
        $output = 0
        for ($inIndex = 0; $inIndex -lt $Signal.Length; $inIndex++) {
            # out index, in index => pattern index
            # 0, 0 => 1
            # 0, 1 => 2
            # 0, 2 => 3
            # 1, 0 => 0
            # 1, 1 => 1
            # 1, 2 => 1
            # 1, 3 => 2
            # 2, 0 => 0
            # 2, 1 => 0
            # 2, 2 => 1
            # 2, 3 => 1
            $patternIndex = [Math]::Floor(($inIndex + 1) / ($outIndex + 1))
            $amp = $Pattern[$patternIndex % $Pattern.Count]
            $output += ([int]($Signal[$inIndex]) - 48) * $amp
            # Write-Verbose "'$([int]($Signal[$inIndex]) - 48)' * '$amp'"
        }

        $output = [Math]::Abs($output) % 10
        Write-Verbose "['$outIndex'] = '$output'"
        $outputSignal += [string]$output
    }

    return $outputSignal
}

function Clear-SignalPhases {
    [CmdletBinding()]
    param(
        [string]
        $Signal,

        [int]
        $Phases = 100
    )

    for ($phaseIndex = 0; $phaseIndex -lt $Phases; $phaseIndex++) {
        $Signal = Clear-Signal $Signal
    }

    return $Signal
}

function Get-Part1Result {
    $signal = ""
    $res = Clear-SignalPhases $signal
    $res

    # 
    # 76795888 - correct
}
